import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { fetchPosts } from './actions';

function App() {
  const dispatch = useDispatch();
  const { random_joke, loading, error } = useSelector(state => state);

  useEffect(() => {
    dispatch(fetchPosts());
  }, [dispatch]);

  return (
    <div className="App">
      {loading && <h2>Loading...</h2>}
      {error && <h2>{error}</h2>}
      {random_joke.map(post => (
        <div key={post.id}>
          <h2>{post.title}</h2>
          <p>{post.body}</p>
        </div>
      ))}
    </div>
  );
}

export default App;
